            double output = TransferFunction(input);
            Console.WriteLine($"Output: {output}");
        }

        static double TransferFunction(double x)
        {
            double a = 4.0;
            double b = 0.5;
            double c = 0.5;
            double y = a * Math.Pow(x - b, 2) + c;
            return y;
        }
    }
}
